// Events
// Eventsの実装は必要に応じて実装してください。
const Events = {
  OPEN_MODAL: "open_modal",
  CLOSE_MODAL: "close_modal",
} as const;

// EventEmitter
type EventCallback = (payload?: any) => void;

class EventEmitterClass {
  _events: Map<string, Set<EventCallback>> = new Map();

  on(event: string, callback: EventCallback) {
    if (!this._events.has(event)) {
      this._events.set(event, new Set());
    }
    this._events.get(event)!.add(callback);
  }

  off(event: string, callback: EventCallback) {
    this._events.get(event)?.delete(callback);
  }

  emit(event: string, payload?: any) {
    this._events.get(event)?.forEach((callback) => callback(payload));
  }
}
const EventEmitter = new EventEmitterClass();


/**
 * モーダルダイアログを管理するクラス
 * @description dialog要素を使用したアクセシブルなモーダルを提供
 */
class Modal {
  /** dialog要素 */
  _dialog: HTMLDialogElement;
  /** 閉じるボタン要素 */
  _closeButton: HTMLElement | null;
  /** モーダルが閉じた後に実行されるコールバック */
  _onCloseCallback?: () => void;

  /**
   * @param element - 対象のdialog要素
   */
  constructor(element: HTMLDialogElement) {
    this._dialog = element;
    this._closeButton = element.querySelector(".js-modalClose");

    this._init();
  }

  /**
   * イベントリスナーを初期化
   */
  private _init() {
    this._handleOpen = this._handleOpen.bind(this);
    this._handleBackdropClick = this._handleBackdropClick.bind(this);
    this._handleDialogClose = this._handleDialogClose.bind(this);

    EventEmitter.on(Events.OPEN_MODAL, this._handleOpen);
    this._closeButton?.addEventListener("click", () => this.close());
    this._dialog.addEventListener("click", this._handleBackdropClick);
    this._dialog.addEventListener("close", this._handleDialogClose);
  }

  /**
   * モーダルを開く
   * @param payload - イベントペイロード
   * @param payload.id - 対象モーダルのdata-modal-id
   * @param payload.onClose - 閉じた後に実行されるコールバック
   */
  private _handleOpen(payload: { id: string; onClose?: () => void }) {
    if (this._dialog.dataset.modalId !== payload.id) return;

    this._onCloseCallback = payload.onClose;
    document.body.classList.add("is-modalOpen");
    this._dialog.showModal();
  }

  /**
   * 背景クリック時の処理
   * @param e - マウスイベント
   */
  private _handleBackdropClick(e: MouseEvent) {
    if (e.target === this._dialog) {
      this._dialog.close();
    }
  }

  /**
   * モーダルが閉じた後の処理
   */
  private _handleDialogClose() {
    document.body.classList.remove("is-modalOpen");
    this._onCloseCallback?.();
    this._onCloseCallback = undefined;
  }

  /**
   * モーダルを閉じる
   */
  private close() {
    this._dialog.close();
  }

  /**
   * イベントリスナーを解除しインスタンスを破棄
   */
  destroy() {
    EventEmitter.off(Events.OPEN_MODAL, this._handleOpen);
    this._closeButton?.removeEventListener("click", this.close);
    this._dialog.removeEventListener("click", this._handleBackdropClick);
    this._dialog.removeEventListener("close", this._handleDialogClose);
  }
}

// 初期化
// 必要に応じて初期化する。
document.querySelectorAll<HTMLDialogElement>(".js-modal").forEach((el) => {
  new Modal(el);
});

document.querySelectorAll<HTMLElement>("[data-modal-target]").forEach((trigger) => {
  trigger.addEventListener("click", () => {
    const targetId = trigger.dataset.modalTarget;
    if (targetId) {
      EventEmitter.emit(Events.OPEN_MODAL, { id: targetId });
    }
  });
});

