/**
 * タブ切り替えを管理するクラス
 * @description アクセシブルなタブUIを提供
 */
class Tab {
  _container: HTMLElement;
  _buttons: HTMLElement[];
  _panels: HTMLElement[];

  constructor(element: HTMLElement) {
    this._container = element;
    this._buttons = Array.from(element.querySelectorAll<HTMLElement>("[data-tab-target]"));
    this._panels = Array.from(element.querySelectorAll<HTMLElement>("[data-tab-panel]"));

    this._init();
  }

  private _init() {
    this._handleClick = this._handleClick.bind(this);
    this._handleKeydown = this._handleKeydown.bind(this);

    this._buttons.forEach((button) => {
      button.addEventListener("click", this._handleClick);
      button.addEventListener("keydown", this._handleKeydown);
    });
  }

  private _handleClick(e: MouseEvent) {
    const target = e.currentTarget as HTMLElement;
    const targetId = target.dataset.tabTarget;

    if (targetId) {
      this._activate(targetId);
    }
  }

  private _handleKeydown(e: KeyboardEvent) {
    const currentIndex = this._buttons.indexOf(e.currentTarget as HTMLElement);
    let nextIndex: number;

    switch (e.key) {
      case "ArrowRight":
        nextIndex = (currentIndex + 1) % this._buttons.length;
        break;
      case "ArrowLeft":
        nextIndex = (currentIndex - 1 + this._buttons.length) % this._buttons.length;
        break;
      case "Home":
        nextIndex = 0;
        break;
      case "End":
        nextIndex = this._buttons.length - 1;
        break;
      default:
        return;
    }

    e.preventDefault();
    this._buttons[nextIndex].focus();
    const targetId = this._buttons[nextIndex].dataset.tabTarget;
    if (targetId) {
      this._activate(targetId);
    }
  }

  private _activate(id: string) {
    // ボタンの状態更新
    this._buttons.forEach((button) => {
      const isActive = button.dataset.tabTarget === id;
      button.classList.toggle("is-active", isActive);
      button.setAttribute("aria-selected", String(isActive));
    });

    // パネルの表示切り替え
    this._panels.forEach((panel) => {
      const isActive = panel.dataset.tabPanel === id;
      panel.classList.toggle("is-active", isActive);
      panel.hidden = !isActive;
    });
  }

  destroy() {
    this._buttons.forEach((button) => {
      button.removeEventListener("click", this._handleClick);
      button.removeEventListener("keydown", this._handleKeydown);
    });
  }
}

// 初期化
document.querySelectorAll<HTMLElement>(".js-tab").forEach((el) => {
  new Tab(el);
});

