/**
 * ツールチップを管理するクラス
 * @description 位置自動調整、遅延表示に対応したツールチップを提供
 */
class Tooltip {
  _container: HTMLElement;
  _trigger: HTMLElement | null;
  _content: HTMLElement | null;
  _position: string;
  _showTimeout: number | null;
  _hideTimeout: number | null;
  _showDelay: number;
  _hideDelay: number;

  constructor(element: HTMLElement, options: { showDelay?: number; hideDelay?: number } = {}) {
    this._container = element;
    this._trigger = element.querySelector(".c-tooltip__trigger");
    this._content = element.querySelector(".c-tooltip__content");
    this._position = element.dataset.tooltipPosition || "top";
    this._showTimeout = null;
    this._hideTimeout = null;
    this._showDelay = options.showDelay ?? 200;
    this._hideDelay = options.hideDelay ?? 100;

    this._init();
  }

  private _init() {
    this._handleMouseEnter = this._handleMouseEnter.bind(this);
    this._handleMouseLeave = this._handleMouseLeave.bind(this);
    this._handleFocus = this._handleFocus.bind(this);
    this._handleBlur = this._handleBlur.bind(this);

    this._trigger?.addEventListener("mouseenter", this._handleMouseEnter);
    this._trigger?.addEventListener("mouseleave", this._handleMouseLeave);
    this._trigger?.addEventListener("focus", this._handleFocus, true);
    this._trigger?.addEventListener("blur", this._handleBlur, true);
  }

  private _handleMouseEnter() {
    this._clearTimeouts();
    this._showTimeout = window.setTimeout(() => this._show(), this._showDelay);
  }

  private _handleMouseLeave() {
    this._clearTimeouts();
    this._hideTimeout = window.setTimeout(() => this._hide(), this._hideDelay);
  }

  private _handleFocus() {
    this._clearTimeouts();
    this._show();
  }

  private _handleBlur() {
    this._clearTimeouts();
    this._hide();
  }

  private _clearTimeouts() {
    if (this._showTimeout) {
      clearTimeout(this._showTimeout);
      this._showTimeout = null;
    }
    if (this._hideTimeout) {
      clearTimeout(this._hideTimeout);
      this._hideTimeout = null;
    }
  }

  private _show() {
    this._adjustPosition();
    this._container.classList.add("is-visible");
  }

  private _hide() {
    this._container.classList.remove("is-visible");
  }

  private _adjustPosition() {
    if (!this._content) return;

    const rect = this._content.getBoundingClientRect();
    const viewportWidth = window.innerWidth;
    const viewportHeight = window.innerHeight;

    let newPosition = this._position;

    // 画面端での位置自動調整
    if (this._position === "top" && rect.top < 0) {
      newPosition = "bottom";
    } else if (this._position === "bottom" && rect.bottom > viewportHeight) {
      newPosition = "top";
    } else if (this._position === "left" && rect.left < 0) {
      newPosition = "right";
    } else if (this._position === "right" && rect.right > viewportWidth) {
      newPosition = "left";
    }

    if (newPosition !== this._position) {
      this._container.dataset.tooltipPosition = newPosition;
    }
  }

  destroy() {
    this._clearTimeouts();
    this._trigger?.removeEventListener("mouseenter", this._handleMouseEnter);
    this._trigger?.removeEventListener("mouseleave", this._handleMouseLeave);
    this._trigger?.removeEventListener("focus", this._handleFocus, true);
    this._trigger?.removeEventListener("blur", this._handleBlur, true);
  }
}

