  const {
    type = "slide",
    perPage = 1,
    gap = 0,
    autoplay = false,
    interval = 0,
    arrows = true,
    pagination = true,
    breakpoints = {},
  } = options;

  return (
    <div
      className="c-carousel splide js-carousel"
      data-splide={JSON.stringify({
        type,
        perPage,
        gap,
        autoplay,
        interval,
        arrows,
        pagination,
