/**
 * アコーディオンの開閉アニメーションを管理するクラス
 * @description heightを使用したスムーズな開閉アニメーションを提供
 */
class Accordion {
  _details: HTMLDetailsElement;
  _summary: HTMLElement | null;
  _content: HTMLElement | null;
  _isAnimating: boolean;

  constructor(element: HTMLDetailsElement) {
    this._details = element;
    this._summary = element.querySelector(".js-accordionSummary");
    this._content = element.querySelector(".js-accordionContent");
    this._isAnimating = false;

    this._init();
  }

  private _init() {
    this._handleClick = this._handleClick.bind(this);
    this._summary?.addEventListener("click", this._handleClick);
  }

  private _handleClick(e: MouseEvent) {
    e.preventDefault();

    if (this._isAnimating) return;

    if (this._details.open) {
      this._close();
    } else {
      this._open();
    }
  }

  private _open() {
    this._isAnimating = true;
    this._details.open = true;

    const height = this._content!.scrollHeight;
    this._content!.style.height = "0px";

    requestAnimationFrame(() => {
      this._content!.style.height = `${height}px`;
    });

    this._content!.addEventListener("transitionend", () => {
      this._content!.style.height = "";
      this._isAnimating = false;
    }, { once: true });
  }

  private _close() {
    this._isAnimating = true;

    const height = this._content!.scrollHeight;
    this._content!.style.height = `${height}px`;

    requestAnimationFrame(() => {
      this._content!.style.height = "0px";
    });

    this._content!.addEventListener("transitionend", () => {
      this._details.open = false;
      this._content!.style.height = "";
      this._isAnimating = false;
    }, { once: true });
  }

  destroy() {
    this._summary?.removeEventListener("click", this._handleClick);
  }
}

// 初期化
document.querySelectorAll<HTMLDetailsElement>(".js-accordion").forEach((el) => {
  new Accordion(el);
});

