/**
 * Splideカルーセルを管理するクラス
 * @description data-splide属性からオプションを取得して初期化、レスポンシブ対応
 */
class Carousel {
  _element: HTMLElement;
  _splide: Splide | null;

  constructor(element: HTMLElement) {
    this._element = element;
    this._splide = null;

    this._init();
  }

  private _init() {
    const options = this._getOptions();
    this._splide = new Splide(this._element, options).mount();
  }

  private _getOptions(): Record<string, unknown> {
    const dataOptions = this._element.dataset.splide;

    if (dataOptions) {
      try {
        return JSON.parse(dataOptions);
      } catch {
        console.warn("Invalid splide options:", dataOptions);
      }
    }

    return {};
  }

  /**
   * 指定のスライドへ移動
   */
  go(index: number) {
    this._splide?.go(index);
  }

  /**
   * 自動再生を開始
   */
  play() {
    this._splide?.Components.Autoplay?.play();
  }

  /**
   * 自動再生を停止
   */
  pause() {
    this._splide?.Components.Autoplay?.pause();
  }

  /**
   * オプションを更新
   */
  refresh() {
    this._splide?.refresh();
  }

  destroy() {
    this._splide?.destroy();
    this._splide = null;
  }
}

// 初期化
document.querySelectorAll<HTMLElement>(".js-carousel").forEach((el) => {
  new Carousel(el);
});

