vec2 centeredUv = vUv - vec2(0.5, 0.5);
centeredUv.x *= aspect;
float dist = length(centeredUv);`

/**
* @description 画像がcanvas全体を覆うように、拡大/縮小(background-size: coverと同じ効果)
* @param {uv, resolution, imageResolution} 
* @return {uv} 画像サイズで補正されたUV座標
*/
`
// 画像アスペクト比を取得する
vec2 imageResolution = vec2(textureSize(uTexture, 0));

vec2 getCoverUv(vec2 uv, vec2 resolution, vec2 imageResolution) {
  vec2 ratio = vec2(
    min((resolution.x / resolution.y) / (imageResolution.x / imageResolution.y), 1.0),
    min((resolution.y / resolution.x) / (imageResolution.y / imageResolution.x), 1.0)
  );
  return uv * ratio + (1.0 - ratio) * 0.5;
}`

/**
* @description pixelNoizeを生成する
*/
`float noiseScale = 6.0;
vec2 pixelatedUv = floor(vUv * uResolution / noiseScale) * noiseScale / uResolution;`

// フラクショナルブラウニアンモーション: 複数オクターブのノイズを重ねる
// より自然で複雑なノイズパターンを生成
`float fbm(vec2 p) {
  float value = 0.0;
  float amplitude = 0.5;
  float frequency = 1.0;

  for (int i = 0; i < 5; i++) {
    value += amplitude * noise(p * frequency);
    amplitude *= 0.5;   // 振幅を半分に
    frequency *= 2.0;   // 周波数を2倍に
  }

  return value;
}`

// パーリンノイズ: 滑らかなランダムパターン
`float hash(vec2 p) {
  return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453);
}

float noise(vec2 p) {
  vec2 i = floor(p);
  vec2 f = fract(p);
  f = f * f * (3.0 - 2.0 * f);

  float a = hash(i);
  float b = hash(i + vec2(1.0, 0.0));
  float c = hash(i + vec2(0.0, 1.0));
  float d = hash(i + vec2(1.0, 1.0));

  return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);
}`
